import java.util.*;
import java.math.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long n = sc.nextLong();
        long m = sc.nextLong();
        long mod = 998244353;

        long[][] d = new long[(int) n + 1][(int) Math.min(m + 1, 200001)];
        long[] a = new long[(int) Math.min(m + 1, 200001)];
        a[1] = 1;
        a[2] = 1;
        for (int i = 3; i <= m; i++) {
            a[i] = (a[i - 1] + a[i - 2]) % mod;
        }

        for (int i = 1; i <= m; i++) {
            d[1][i] = a[i];
        }

        for (int i = 2; i <= n; i++) {
            for (int j = 1; j <= m; j++) {
                for (int k = 1; k <= j; k++) {
                    d[i][j] = (d[i][j] + d[i - 1][k]) % mod;
                }
            }
        }

        System.out.println(d[(int) n][(int) m]);
    }
}
