#include <iostream>
#include <vector>

using namespace std;

int main() {
    long long H, W, N, K;
    cin >> H >> W >> N >> K;

    vector<vector<long long>> field(H, vector<long long>(W, 0));
    long long ans = 0;

    for (long long i = 0; i < H; ++i) {
        for (long long j = 0; j < W; ++j) {
            cout << "? " << i << " " << j << " " << i << " " << j << endl;
            long long p;
            cin >> p;
            field[i][j] = p;
            ans = (ans + field[i][j] * (long long)pow(2, i * W + j)) % K;
        }
    }

    cout << "! " << ans << endl;
    return 0;
}
