#include <stdio.h>

int main() {
    int H, W;
    scanf("%d %d", &H, &W);
    char a[H][W];
    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            scanf(" %c", &a[i][j]);
        }
    }
    int possible = 1;
    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            if (a[i][j] == '#') {
                int k = 0;
                for (int x = 0; x <= i; x++) {
                    for (int y = 0; y <= j; y++) {
                        if (x == 0 && y == 0) continue;
                        if (a[x][y] == '#') k++;
                    }
                }
                if (k != i + j) {
                    possible = 0;
                    break;
                }

            }
        }
        if (!possible) break;
    }
    if (possible && a[H-1][W-1] == '#') {
        printf("Possible\n");
    } else {
        printf("Impossible\n");
    }

    return 0;
}
