#include <iostream>
#include <vector>

using namespace std;

int main() {
  int h, w;
  cin >> h >> w;

  vector<string> a(h);
  for (int i = 0; i < h; ++i) {
    cin >> a[i];
  }

  bool possible = true;
  for (int i = 0; i < h; ++i) {
    for (int j = 0; j < w; ++j) {
      if (a[i][j] == '#') {
        bool reachable = false;
        for (int k = 0; k <= i; ++k) {
          for (int l = 0; l <= j; ++l) {
            if (k == 0 && l == 0) {
              reachable = true;
              break;
            }
            if (a[k][l] == '#' && (k == 0 || a[k - 1][l] == '#') && (l == 0 || a[k][l - 1] == '#'))
            {
                reachable = true;
                break;
            }
          }
          if(reachable) break;
        }

        if (!reachable) {
          possible = false;
          break;
        }
      }
    }
    if (!possible) break;
  }

  if (possible) {
    cout << "Possible" << endl;
  } else {
    cout << "Impossible" << endl;
  }

  return 0;
}
