import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int H = scanner.nextInt();
        int W = scanner.nextInt();
        char[][] a = new char[H][W];
        for (int i = 0; i < H; i++) {
            String row = scanner.next();
            for (int j = 0; j < W; j++) {
                a[i][j] = row.charAt(j);
            }
        }
        boolean possible = true;
        for (int i = 0; i < H; i++) {
            for (int j = 0; j < W; j++) {
                if (a[i][j] == '#') {
                    boolean found = false;
                    for (int k = 0; k <= i; k++) {
                        for (int l = 0; l <= j; l++) {
                            if (k == 0 && l == 0) {
                                found = true;
                                break;
                            }
                            if (a[k][l] == '#') {
                                if (k == i && l == j) {
                                    found = true;
                                    break;
                                }
                                if (k + 1 == i && l == j) {
                                    found = true;
                                    break;
                                }
                                if (k == i && l + 1 == j) {
                                    found = true;
                                    break;
                                }
                            }
                        }
                        if (found) break;
                    }
                    if (!found) possible = false;
                }
            }
        }
        if (possible) {
            System.out.println("Possible");
        } else {
            System.out.println("Impossible");
        }
    }
}
