H, W = map(int, input().split())
grid = [input() for _ in range(H)]

possible = True
for i in range(H):
    for j in range(W):
        if grid[i][j] == '#':
            if i == 0 and j == 0:
                continue
            if grid[i-1][j] != '#' and i > 0 and j==0 :
                possible = False
                break
            if grid[i][j-1] != '#' and i == 0 and j > 0 :
                possible = False
                break
            if grid[i-1][j] != '#' and grid[i][j-1] != '#' and i>0 and j>0:
                possible = False
                break

    if not possible:
        break

if possible:
    count = 0
    for i in range(H):
        for j in range(W):
            if grid[i][j] == '#':
                count +=1
    if count != H+W-1:
        possible = False


if possible:
    print("Possible")
else:
    print("Impossible")

