#include <stdio.h>

int main() {
    int N;
    scanf("%d", &N);
    int p[N];
    for (int i = 0; i < N; i++) {
        scanf("%d", &p[i]);
        p[i]--; 
    }
    long long a[N], b[N];
    for (int i = 0; i < N; i++) {
        a[i] = i + 1;
    }
    for (int i = 0; i < N; i++) {
        b[i] = (long long)N * (N + 1) / 2 - i;
    }
    for (int i = 0; i < N; i++) {
        printf("%lld%c", a[i], i == N - 1 ? '\n' : ' ');
    }
    for (int i = 0; i < N; i++) {
        printf("%lld%c", b[i], i == N - 1 ? '\n' : ' ');
    }
    return 0;
}
