import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[] p = new int[n];
        for (int i = 0; i < n; i++) {
            p[i] = sc.nextInt() - 1;
        }
        long[] a = new long[n];
        long[] b = new long[n];
        for (int i = 0; i < n; i++) {
            a[i] = i + 1;
            b[i] = n - i;
        }
        long[] sum = new long[n];
        for (int i = 0; i < n; i++) {
            sum[i] = a[p[i]] + b[p[i]];
        }
        for (int i = 0; i < n; i++) {
            System.out.print(a[i] + (i == n - 1 ? "" : " "));
        }
        System.out.println();
        for (int i = 0; i < n; i++) {
            System.out.print(b[i] + (i == n - 1 ? "" : " "));
        }
        System.out.println();
    }
}
