#include <stdio.h>

int main() {
    long long N, d1, x;
    scanf("%lld %lld %lld", &N, &d1, &x);
    double ans = 0;
    for (long long i = 1; i <= N; i++) {
        long long d = d1 + (2 * i - 2) * x;
        ans += (double)d * (i + 1) / 2.0;
    }
    printf("%.15lf\n", ans);
    return 0;
}
