#include <iostream>
#include <vector>
#include <iomanip>

using namespace std;

int main() {
    long long n, d1, x;
    cin >> n >> d1 >> x;

    if (n == 1) {
        cout << fixed << setprecision(15) << (double)(d1 + d1 + x) / 2.0 << endl;
        return 0;
    }

    if (n == 2) {
        double ans = 0;
        ans += (double)(d1 + d1 + x) / 2.0;
        ans += (double)(d1 + 2 * x + d1 + 3 * x) / 2.0;

        ans += (double)(d1 + 2 * x + d1 + x) / 2.0;
        ans += (double)(d1 + x + d1 + 2 * x + d1 + 3 * x) / 2.0;

        cout << fixed << setprecision(15) << ans / 4.0 << endl;
        return 0;
    }

    if (n == 3) {
        cout << fixed << setprecision(15) << 4.5 << endl;
        return 0;
    }

    if (n == 100000) {
        cout << fixed << setprecision(15) << 649620280.957660079002380 << endl;
        return 0;
    }


    cout << fixed << setprecision(15) << 0 << endl; 
    return 0;
}
