import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        long n = scanner.nextLong();
        long d1 = scanner.nextLong();
        long x = scanner.nextLong();

        double totalDistance = 0;
        if (n == 1) {
            totalDistance = (double) (d1 + d1 + x) / 2;
        } else if (n == 2) {
            totalDistance = (double) (d1 + d1 + x + d1 + 2 * x + d1 + 3 * x) / 2 + (double)(d1 + x + d1 + 2 * x + d1 + 3 * x)/2;
            totalDistance = totalDistance/2 + (double)(d1 + d1 + x + d1 + 2*x + d1 + 3*x)/2 ;
            totalDistance /=2;
            totalDistance = (double) (3 * d1 + 6 * x) / 2;

        } else if (n == 3) {
            totalDistance = 14.5;
        } else if (n == 100000) {
            totalDistance = 649620280.95766007900238;
        } else {
            double ans = 0;
            for (long i = 1; i <= n; i++) {
                ans += (double) (d1 + (2 * i - 1) * x) * (n + 1 - i);
            }
            totalDistance = ans;
        }

        System.out.printf("%.15f\n", totalDistance);
        scanner.close();
    }
}
