#include <stdio.h>
#include <stdlib.h>

int main() {
    long long N, E, T;
    scanf("%lld %lld %lld", &N, &E, &T);
    long long x[N];
    for (long long i = 0; i < N; i++) {
        scanf("%lld", &x[i]);
    }
    long long total_time = 0;
    long long current_pos = 0;
    long long current_time = 0;
    for (long long i = 0; i < N; i++) {
        total_time += abs(x[i] - current_pos);
        current_pos = x[i];
        current_time += abs(x[i] - current_pos) + T;

    }
    total_time += abs(E - current_pos);
    current_time += abs(E - current_pos);

    printf("%lld\n", current_time);
    return 0;
}
