#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
  long long n, e, t;
  cin >> n >> e >> t;

  vector<long long> x(n);
  for (int i = 0; i < n; ++i) {
    cin >> x[i];
  }

  long long total_time = 0;
  long long current_pos = 0;

  for (int i = 0; i < n; ++i) {
    total_time += abs(x[i] - current_pos);
    current_pos = x[i];
    total_time += t;
  }

  total_time += abs(e - current_pos);

  cout << total_time << endl;
  return 0;
}
