import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long e = sc.nextLong();
        long t = sc.nextLong();
        long[] x = new long[n];
        for (int i = 0; i < n; i++) {
            x[i] = sc.nextLong();
        }
        long ans = 0;
        long cur = 0;
        long last = 0;
        for (int i = 0; i < n; i++) {
            ans += Math.abs(x[i] - cur);
            cur = x[i];
            long next = (i == n - 1 ? e : x[i + 1]);
            ans += t;
            cur = next;
        }
        System.out.println(ans);
    }
}
