#include <stdio.h>
#include <stdlib.h>
#include <limits.h>

#define MAXN 131072

int N;
int a[MAXN], v[MAXN];
int parent[MAXN];
int leafCount;
int leaves[MAXN];

void dfs(int u) {
    int isLeaf = 1;
    for (int i = 0; i < N - 1; i++) {
        if (a[i] == u) {
            isLeaf = 0;
            parent[i + 1] = u;
            dfs(i + 1);
        }
    }
    if (isLeaf) {
        leaves[leafCount++] = u;
    }
}

