#include <stdio.h>

int main() {
    int N, K;
    scanf("%d %d", &N, &K);
    long long A[N], B[N];
    for (int i = 0; i < N; i++) {
        scanf("%lld %lld", &A[i], &B[i]);
    }
    long long max_utility = 0;
    for (int i = 0; i < (1 << N); i++) {
        long long current_or = 0;
        long long current_utility = 0;
        for (int j = 0; j < N; j++) {
            if ((i >> j) & 1) {
                current_or |= A[j];
                current_utility += B[j];
            }
        }
        if (current_or <= K) {
            max_utility = (max_utility > current_utility) ? max_utility : current_utility;
        }
    }
    printf("%lld\n", max_utility);
    return 0;
}
