#include <stdio.h>
#include <string.h>

int main() {
    int n;
    scanf("%d", &n);
    char s0[n + 1], t[n + 1];
    scanf("%s", s0);
    scanf("%s", t);
    if (strcmp(s0, t) == 0) {
        printf("0\n");
        return 0;
    }
    char s[n + 1];
    int i = 1;
    while (i <= n) {
        strcpy(s, s0);
        int j = 1;
        while (j < n) {
            if (s[j] != t[j]) {
                s[j] = s[j - 1];
            }
            j++;
        }
        if (strcmp(s, t) == 0) {
            printf("%d\n", i);
            return 0;
        }
        strcpy(s0, s);
        i++;
        if (i > n) break;
    }
    printf("-1\n");
    return 0;
}
