import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        String s0 = sc.next();
        String t = sc.next();

        if (s0.equals(t)) {
            System.out.println(0);
            return;
        }

        Queue<String> q = new LinkedList<>();
        q.offer(s0);
        Set<String> visited = new HashSet<>();
        visited.add(s0);
        int day = 1;

        while (!q.isEmpty()) {
            int size = q.size();
            for (int i = 0; i < size; i++) {
                String curr = q.poll();
                if (curr.equals(t)) {
                    System.out.println(day);
                    return;
                }
                for (int j = 0; j < (1 << (n - 1)); j++) {
                    StringBuilder next = new StringBuilder(curr.charAt(0) + "");
                    char prev = curr.charAt(0);
                    for (int k = 0; k < n - 1; k++) {
                        char c;
                        if ((j >> k) % 2 == 0) {
                            c = curr.charAt(k + 1);
                        } else {
                            c = prev;
                        }
                        next.append(c);
                        prev = c;
                    }
                    String nextStr = next.toString();
                    if (!visited.contains(nextStr)) {
                        visited.add(nextStr);
                        q.offer(nextStr);
                    }
                }
            }
            day++;
            if(day > n + 1) break;
        }
        System.out.println(-1);
    }
}
