def solve():
    n = int(input())
    s0 = input()
    t = input()

    if s0 == t:
        print(0)
        return

    q = [(s0, 0)]
    visited = {s0}

    while q:
        curr_s, curr_day = q.pop(0)

        next_s = list(curr_s)
        possible = True
        for i in range(1,n):
            if next_s[i] != t[i] and next_s[i] != next_s[i-1]:
                possible = False
                break

        if possible:
          next_s_str = "".join(next_s)
          if next_s_str == t:
            print(curr_day + 1)
            return


        for i in range(1, n):
            next_s_temp = list(curr_s)
            next_s_temp[i] = curr_s[i-1]
            next_s_str = "".join(next_s_temp)
            if next_s_str not in visited:
                visited.add(next_s_str)
                q.append((next_s_str, curr_day + 1))

    print(-1)

solve()
