#include <iostream>

using namespace std;

int main() {
  int a, b, c;
  cin >> a >> b >> c;

  if ((a + b + c) % 2 != 0) {
    cout << "No" << endl;
    return 0;
  }

  int sum = a + b + c;
  int halfSum = sum / 2;

  if (a == halfSum || b == halfSum || c == halfSum ||
      a + b == halfSum || a + c == halfSum || b + c == halfSum) {
    cout << "Yes" << endl;
  } else {
    cout << "No" << endl;
  }
  return 0;
}
