#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n, k;
  cin >> n >> k;

  vector<pair<long long, long long>> items(n);
  for (int i = 0; i < n; ++i) {
    cin >> items[i].first >> items[i].second;
  }

  long long max_utility = 0;
  for (int i = 0; i < (1 << n); ++i) {
    long long current_or = 0;
    long long current_utility = 0;
    for (int j = 0; j < n; ++j) {
      if ((i >> j) & 1) {
        current_or |= items[j].first;
        current_utility += items[j].second;
      }
    }
    if (current_or <= k) {
      max_utility = max(max_utility, current_utility);
    }
  }

  cout << max_utility << endl;
  return 0;
}
