#include <stdio.h>

int main() {
    int W, H, N;
    scanf("%d %d %d", &W, &H, &N);
    int min_x = 0, max_x = W, min_y = 0, max_y = H;
    for (int i = 0; i < N; i++) {
        int x, y, a;
        scanf("%d %d %d", &x, &y, &a);
        if (a == 1) max_x = x;
        else if (a == 2) min_x = x;
        else if (a == 3) max_y = y;
        else min_y = y;
    }
    int area = (max_x - min_x) * (max_y - min_y);
    printf("%d\n", area);
    return 0;
}
