#include <iostream>

using namespace std;

int main() {
    long long W, H, N;
    cin >> W >> H >> N;

    long long minX = 0, maxX = W, minY = 0, maxY = H;

    for (int i = 0; i < N; ++i) {
        long long x, y, a;
        cin >> x >> y >> a;
        if (a == 1) minX = max(minX, x);
        else if (a == 2) maxX = min(maxX, x);
        else if (a == 3) minY = max(minY, y);
        else maxX = min(maxY, y);
    }

    long long area = max(0LL, maxX - minX) * max(0LL, maxY - minY);
    cout << area << endl;

    return 0;
}
