import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int W = sc.nextInt();
        int H = sc.nextInt();
        int N = sc.nextInt();
        int minX = 0, maxX = W, minY = 0, maxY = H;
        for (int i = 0; i < N; i++) {
            int x = sc.nextInt();
            int y = sc.nextInt();
            int a = sc.nextInt();
            if (a == 1) maxX = Math.min(maxX, x);
            else if (a == 2) minX = Math.max(minX, x);
            else if (a == 3) maxY = Math.min(maxY, y);
            else minY = Math.max(minY, y);
        }
        int area = Math.max(0, maxX - minX) * Math.max(0, maxY - minY);
        System.out.println(area);
        sc.close();
    }
}
