import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        String s = scanner.next();
        int n = s.length();
        int blackCount = 0;
        for (int i = 0; i < n; i++) {
            if (s.charAt(i) == 'B') {
                blackCount++;
            }
        }
        int whiteCount = n - blackCount;
        int minStones = Math.min(blackCount, whiteCount);
        System.out.println(minStones);
        scanner.close();
    }
}
