#include <stdio.h>
#include <stdlib.h>
#include <limits.h>

long long min(long long a, long long b) {
    return a < b ? a : b;
}

int main() {
    long long n, t;
    scanf("%lld %lld", &n, &t);
    long long a[n];
    for (long long i = 0; i < n; i++) {
        scanf("%lld", &a[i]);
    }
    long long max_profit = 0;
    long long min_cost = LLONG_MAX;
    for (long long i = 0; i < (1LL << (n -1)); ++i) {
        long long current_profit = 0;
        long long num_transactions = 0;
        long long current_apples = 0;
        long long current_cost = 0;

        for (long long j = 0; j < n-1; j++){
            if ((i >> j) & 1) {
                if (a[j+1] > a[j]){
                    current_profit += (a[j+1] - a[j]);
                    num_transactions += 2;
                }
            }
        }

        if (num_transactions <= t)
          max_profit = (max_profit > current_profit) ? max_profit : current_profit;

    }

    for (long long i = 0; i < n; i++) {
