#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    long long n, t;
    cin >> n >> t;

    vector<long long> a(n);
    for (int i = 0; i < n; ++i) {
        cin >> a[i];
    }

    long long max_profit = 0;
    for (int i = 0; i < (1 << (n - 1)); ++i) {
        long long current_profit = 0;
        long long current_apples = 0;
        long long total_transactions = 0;
        vector<pair<long long, long long>> transactions;

        for (int j = 0; j < n - 1; ++j) {
            if ((i >> j) & 1) { 
                transactions.push_back({j, j + 1});
            }
        }

        for (int k = 0; k < transactions.size(); k++) {
          long long buy_town = transactions[k].first;
          long long sell_town = transactions[k].second;
          if (a[sell_town] > a[buy_town]) {
            long long profit_per_apple = a[sell_town] - a[buy_town];
            long long num_apples = min(t - total_transactions, (long long)1000000000);
            current_profit += profit_per_apple * num_apples;
            total_transactions += num_apples * 2;
          }
        }
        max_profit = max(max_profit, current_profit);
    }

    long long min_cost = -1;
    for (int i = 0; i < n; ++i) {
        long long cost1 = max_profit - (max_profit - 1);
        long long cost2 = max_profit - (max_profit -1);
        long long cost = min(cost1, cost2);
        if (min_cost == -1 || cost < min_cost) {
            min_cost = cost;
        }
    }

    cout << min_cost << endl;

    return 0;
}
