import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long t = sc.nextLong();
        long[] a = new long[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextLong();
        }
        long maxProfit = 0;
        for (int i = 0; i < (1 << (n - 1)); i++) {
            long curProfit = 0;
            long curApples = 0;
            long trades = 0;
            long minPrice = -1;
            long maxPrice = -1;
            for (int j = 0; j < n; j++) {
                if (j > 0 && (i & (1 << (j - 1))) != 0) {
                    if (minPrice == -1 || a[j] < minPrice) {
                        minPrice = a[j];
                    }
                    if (maxPrice == -1 || a[j] > maxPrice) {
                        maxPrice = a[j];
                    }
                } else if (j > 0) {
                    if (minPrice != -1 && maxPrice != -1) {
                        long profit = maxPrice - minPrice;
                        long numTrades = Math.min(t - trades, 1);
                        curProfit += profit * numTrades;
                        trades += numTrades * 2;
                        minPrice = -1;
                        maxPrice = -1;
                    }
                }
                if (j == n - 1) {
                    if (minPrice != -1 && maxPrice != -1) {
                        long profit = maxPrice - minPrice;
                        long numTrades = Math.min(t - trades, 1);
                        curProfit += profit * numTrades;
                        trades += numTrades * 2;
                        minPrice = -1;
                        maxPrice = -1;
                    }
                }
            }
            maxProfit = Math.max(maxProfit, curProfit);
        }
        long minCost = Long.MAX_VALUE;
        for (int i = 0; i < n; i++) {
            long cost1 = Math.abs(a[i] - (a[i] + 1));
            long cost2 = Math.abs(a[i] - (a[i] - 1));
            long profit1 = maxProfit - cost1;
            long profit2 = maxProfit - cost2;
            if (profit1 < maxProfit) minCost = Math.min(minCost, cost1);
            if (profit2 < maxProfit) minCost = Math.min(minCost, cost2);
        }

        System.out.println(minCost);
    }
}
