#include <stdio.h>
#include <string.h>
#include <stdlib.h>

int main() {
    char S[100001];
    scanf("%s", S);
    int len = strlen(S);
    int b_count = 0;
    int w_count = 0;
    for (int i = 0; i < len; i++) {
        if (S[i] == 'B')
            b_count++;
        else
            w_count++;
    }
    if (b_count == 0 || w_count == 0) {
        printf("0\n");
        return 0;
    }
    int min_stones = len;
    int cur_b = 0;
    int cur_w = 0;
    for (int i = 0; i < len; i++) {
        if (S[i] == 'B')
            cur_b++;
        else
            cur_w++;
        if (cur_b > 0 && cur_w > 0) {
            int stones = cur_b + cur_w;
            min_stones = (min_stones > stones) ? stones : min_stones;

        }
    }

    cur_b = 0;
    cur_w = 0;
    for (int i = len -1; i >=0; i--) {
        if (S[i] == 'B')
            cur_b++;
        else
            cur_w++;
        if (cur_b > 0 && cur_w > 0) {
            int stones = cur_b + cur_w;
            min_stones = (min_stones > stones) ? stones : min_stones;

        }
    }

    printf("%d\n", min_stones);
    return 0;
}
