#include <iostream>
#include <string>
#include <algorithm>

using namespace std;

int solve() {
  string s;
  cin >> s;

  int n = s.length();
  if (n == 0) return 0;

  int min_stones = n;

  for (int i = 0; i < (1 << 2); ++i) {
    string cur_s = s;
    int stones_added = 0;

    if ((i >> 0) & 1) { 
      cur_s = 'W' + cur_s;
      stones_added++;
    }
    if ((i >> 1) & 1) {
      cur_s = cur_s + 'W';
      stones_added++;
    }


    bool all_same = true;
    for (size_t j = 1; j < cur_s.length(); ++j) {
      if (cur_s[j] != cur_s[0]) {
        all_same = false;
        break;
      }
    }

    if (all_same) {
      min_stones = min(min_stones, stones_added);
    }


    cur_s = s;
    stones_added = 0;
    if ((i >> 0) & 1) {
      cur_s = 'B' + cur_s;
      stones_added++;
    }
    if ((i >> 1) & 1) {
      cur_s = cur_s + 'B';
      stones_added++;
    }

    all_same = true;
    for (size_t j = 1; j < cur_s.length(); ++j) {
      if (cur_s[j] != cur_s[0]) {
        all_same = false;
        break;
      }
    }
    if (all_same) {
      min_stones = min(min_stones, stones_added);
    }
  }


  bool all_same = true;
  for (size_t j = 1; j < s.length(); ++j) {
    if (s[j] != s[0]) {
      all_same = false;
      break;
    }
  }
  if(all_same) min_stones = min(min_stones, 0);

  return min_stones;
}

int main() {
  cout << solve() << endl;
  return 0;
}
