import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String s = sc.next();
        int n = s.length();
        int ans = n;
        for (int i = 0; i < 2; i++) {
            int cnt = 0;
            char c = (i == 0) ? 'B' : 'W';
            char other = (i == 0) ? 'W' : 'B';
            boolean flag = false;
            for (int j = 0; j < n; j++) {
                if (s.charAt(j) == other) {
                    flag = true;
                    break;
                }
            }
            if (!flag) {
                ans = 0;
                break;
            }
            int cur = 0;
            boolean f = false;
            for (int j = 0; j < n; j++) {
                if (s.charAt(j) == other) {
                    if (f) cur++;
                    else {
                        f = true;
                        cur++;
                    }
                }
            }
            ans = Math.min(ans, cur);

        }
        System.out.println(ans);
    }
}
