import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long t = sc.nextLong();
        long[] a = new long[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextLong();
        }
        long maxProfit = 0;
        for (int i = 0; i < (1 << (n - 1)); i++) {
            long curProfit = 0;
            long curApples = 0;
            long trades = 0;
            long curPrice = a[0];
            for (int j = 0; j < n - 1; j++) {
                if ((i >> j) % 2 == 1) {
                    long diff = a[j + 1] - curPrice;
                    if (diff > 0) {
                        long buy = Math.min(t - trades, (long) (t / 2));
                        curProfit += buy * diff;
                        curApples += buy;
                        trades += buy * 2;

                    }
                    curPrice = a[j + 1];
                }
            }
            maxProfit = Math.max(maxProfit, curProfit);
        }
        long minCost = Long.MAX_VALUE;
        for (int i = 0; i < n; i++) {
            long cost1 = Math.abs(a[i] - (a[i] + 1));
            long cost2 = Math.abs(a[i] - (a[i] -1));
            minCost = Math.min(minCost,Math.min(cost1,cost2));

        }
        System.out.println(minCost);
    }
}
