#include <stdio.h>
#include <stdlib.h>

#define MAXN 100005

int n, k;
int a[MAXN], b[MAXN];
int v[MAXN], p[MAXN];
int ans[MAXN];
int adj[MAXN][2];
int deg[MAXN];

int main() {
    scanf("%d", &n);
    for (int i = 0; i < n - 1; i++) {
        scanf("%d %d", &a[i], &b[i]);
        a[i]--;
        b[i]--;
        adj[a[i]][deg[a[i]]++] = b[i];
        adj[b[i]][deg[b[i]]++] = a[i];
    }
    scanf("%d", &k);
    for (int i = 0; i < k; i++) {
        scanf("%d %d", &v[i], &p[i]);
        v[i]--;
        ans[v[i]] = p[i];
    }
    int q[MAXN], head = 0, tail = 0;
    for (int i = 0; i < n; i++) {
        if (ans[i] != 0) {
            q[tail++] = i;
        }
    }
    while (head < tail) {
        int u = q[head++];
        for (int i = 0; i < deg[u]; i++) {
            int w = adj[u][i];
            if (ans[w] == 0) {
                ans[w] = ans[u] + 1;
                q[tail++] = w;
            } else if (ans[w] != ans[u] + 1 && ans[w] != ans[u] - 1) {
                printf("No\n");
                return 0;
            }
        }
    }
    for (int i = 0; i < n; i++) {
        if (ans[i] == 0) {
            printf("No\n");
            return 0;
        }
    }
    printf("Yes\n");
    for (int i = 0; i < n; i++) {
        printf("%d\n", ans[i]);
    }
    return 0;
}
