import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        List<List<Integer>> adj = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            adj.add(new ArrayList<>());
        }
        for (int i = 0; i < n - 1; i++) {
            int u = sc.nextInt() - 1;
            int v = sc.nextInt() - 1;
            adj.get(u).add(v);
            adj.get(v).add(u);
        }
        int k = sc.nextInt();
        Map<Integer, Integer> assigned = new HashMap<>();
        for (int i = 0; i < k; i++) {
            assigned.put(sc.nextInt() - 1, sc.nextInt());
        }
        int[] ans = new int[n];
        Arrays.fill(ans, -1);
        for (Map.Entry<Integer, Integer> entry : assigned.entrySet()) {
            ans[entry.getKey()] = entry.getValue();
        }
        Queue<Integer> q = new LinkedList<>();
        for (Map.Entry<Integer, Integer> entry : assigned.entrySet()) {
            q.offer(entry.getKey());
        }
        while (!q.isEmpty()) {
            int u = q.poll();
            for (int v : adj.get(u)) {
                if (ans[v] == -1) {
                    ans[v] = ans[u] + 1;
                    q.offer(v);
                } else if (Math.abs(ans[u] - ans[v]) != 1) {
                    System.out.println("No");
                    return;
                }
            }
        }

        boolean possible = true;
        for(int i = 0; i < n; i++){
            if(ans[i] == -1){
                possible = false;
                break;
            }
        }
        if(possible){
            System.out.println("Yes");
            for (int i = 0; i < n; i++) {
                System.out.println(ans[i]);
            }
        } else {
            System.out.println("No");
        }
    }
}
