#include <stdio.h>
#include <stdlib.h>

int main() {
    long long W, H, N;
    scanf("%lld %lld %lld", &W, &H, &N);
    long long x[N], y[N];
    for (int i = 0; i < N; i++) {
        scanf("%lld %lld", &x[i], &y[i]);
    }
    long long max_perimeter = 0;
    for (int i = 0; i < (1 << (2 * N)); i++) {
        long long min_x = 0, max_x = W, min_y = 0, max_y = H;
        for (int j = 0; j < N; j++) {
            if ((i >> (2 * j)) & 1) {
                max_x = (long long)fmin(max_x, x[j]);
            } else if ((i >> (2 * j + 1)) & 1) {
                min_x = (long long)fmax(min_x, x[j]);
            }
            if ((i >> (2 * j + 2 * N)) & 1) {
                max_y = (long long)fmin(max_y, y[j]);
            } else if ((i >> (2 * j + 2 * N +1)) & 1) {
                min_y = (long long)fmax(min_y, y[j]);
            }
        }
        if (max_x > min_x && max_y > min_y) {
            max_perimeter = (long long)fmax(max_perimeter, 2 * (max_x - min_x + max_y - min_y));
        }
    }
    printf("%lld\n", max_perimeter);
    return 0;
}
