#include <iostream>
#include <algorithm>
#include <vector>

using namespace std;

int main() {
    long long w, h;
    int n;
    cin >> w >> h >> n;

    vector<pair<long long, long long>> points(n);
    for (int i = 0; i < n; ++i) {
        cin >> points[i].first >> points[i].second;
    }

    long long max_perimeter = 0;
    for (int i = 0; i < (1 << (2 * n)); ++i) {
        long long min_x = 0, max_x = w, min_y = 0, max_y = h;
        vector<pair<long long, long long>> painted_regions;

        for (int j = 0; j < n; ++j) {
            int choice = (i >> (2 * j)) & 3;
            if (choice == 0) max_x = min(max_x, points[j].first);
            else if (choice == 1) min_x = max(min_x, points[j].first);
            else if (choice == 2) max_y = min(max_y, points[j].second);
            else min_y = max(min_y, points[j].second);
        }

        if (max_x >= min_x && max_y >= min_y) {
            max_perimeter = max(max_perimeter, 2 * (max_x - min_x + max_y - min_y));
        }
    }

    cout << max_perimeter << endl;
    return 0;
}
