W, H, N = map(int, input().split())
points = []
for i in range(N):
    x, y = map(int, input().split())
    points.append((x, y))

max_perimeter = 0
for i in range(1 << (2 * N)):
    x_values = [0, W]
    y_values = [0, H]
    for j in range(N):
        x, y = points[j]
        if (i >> (2 * j)) & 1:
            x_values.append(x)
        else:
            x_values.append(x)
        if (i >> (2 * j + 1)) & 1:
            y_values.append(y)
        else:
            y_values.append(y)

    x_values = sorted(list(set(x_values)))
    y_values = sorted(list(set(y_values)))

    max_width = 0
    max_height = 0

    for k in range(len(x_values) -1):
        for l in range(len(y_values) -1):
            width = x_values[k+1] - x_values[k]
            height = y_values[l+1] - y_values[l]

            valid = True
            for m in range(N):
                x,y = points[m]
                if (i >> (2*m)) & 1:
                    if x <= x_values[k]:
                        valid = False
                        break
                else:
                    if x >= x_values[k+1]:
                        valid = False
                        break
                if (i >> (2*m+1)) & 1:
                    if y <= y_values[l]:
                        valid = False
                        break
                else:
                    if y >= y_values[l+1]:
                        valid = False
                        break
            if valid:
                max_width = max(max_width, width)
                max_height = max(max_height, height)

    perimeter = 2 * (max_width + max_height)
    max_perimeter = max(max_perimeter, perimeter)
print(max_perimeter)

