#include <stdio.h>
#include <string.h>

int main() {
    int n;
    char s[101], t[101];
    scanf("%d", &n);
    scanf("%s", s);
    scanf("%s", t);
    int len = n;
    for (int i = 0; i < n; i++) {
        int j = 0;
        while (j < n && s[i + j] == t[j]) {
            j++;
        }
        len = n + n - j;
        if (j == n)
            break;

    }
    printf("%d\n", len);
    return 0;
}
