#include <iostream>
#include <string>

using namespace std;

int main() {
  int n;
  cin >> n;
  string s, t;
  cin >> s >> t;

  int overlap = 0;
  for (int i = 1; i <= n; ++i) {
    bool match = true;
    for (int j = 0; j < i; ++j) {
      if (s[n - i + j] != t[j]) {
        match = false;
        break;
      }
    }
    if (match) {
      overlap = i;
    }
  }

  cout << n + n - overlap << endl;
  return 0;
}
