import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        String s = sc.next();
        String t = sc.next();
        int overlap = 0;
        for (int i = 1; i <= n; i++) {
            boolean match = true;
            for (int j = 0; j < i; j++) {
                if (s.charAt(n - i + j) != t.charAt(j)) {
                    match = false;
                    break;
                }
            }
            if (match) {
                overlap = i;
            }
        }
        System.out.println(n + n - overlap);
    }
}
