#include <stdio.h>

int main() {
    long long n;
    scanf("%lld", &n);
    double x[n];
    for (long long i = 0; i < n; i++) {
        scanf("%lf", &x[i]);
    }
    long long m, k;
    scanf("%lld %lld", &m, &k);
    long long a[m];
    for (long long i = 0; i < m; i++) {
        scanf("%lld", &a[i]);
        a[i]--; 
    }
    double exp_x[n];
    for (long long i = 0; i < n; i++) {
        exp_x[i] = x[i];
    }

    for (long long iter = 0; iter < k; iter++) {
        double next_exp_x[n];
        for (long long i = 0; i < n; i++) {
            next_exp_x[i] = exp_x[i];
        }
        for (long long i = 0; i < m; i++) {
            long long rabbit_index = a[i];
            double avg = (exp_x[rabbit_index -1] + exp_x[rabbit_index + 1]) / 2.0;
            next_exp_x[rabbit_index] = 2 * avg - exp_x[rabbit_index];

        }
        for (long long i = 0; i < n; i++) {
            exp_x[i] = next_exp_x[i];
        }
    }

    for (long long i = 0; i < n; i++) {
        printf("%.1lf\n", exp_x[i]);
    }
    return 0;
}
