#include <iostream>
#include <vector>
#include <iomanip>

using namespace std;

int main() {
    long long n;
    cin >> n;

    vector<double> x(n);
    for (int i = 0; i < n; ++i) {
        cin >> x[i];
    }

    long long m, k;
    cin >> m >> k;

    vector<long long> a(m);
    for (int i = 0; i < m; ++i) {
        cin >> a[i];
        a[i]--; 
    }

    vector<double> expected_x = x;

    for (long long set_num = 0; set_num < min((long long)1000,k); ++set_num) {
        vector<double> next_expected_x = expected_x;
        for (long long jump_index = 0; jump_index < m; ++jump_index) {
            long long rabbit_index = a[jump_index];
            double new_pos = 0.5 * (2 * expected_x[rabbit_index] - expected_x[rabbit_index -1] ) + 0.5 * (2 * expected_x[rabbit_index] - expected_x[rabbit_index + 1]);
            next_expected_x[rabbit_index] = new_pos;
        }
        expected_x = next_expected_x;
    }

    cout << fixed << setprecision(10);
    for (int i = 0; i < n; ++i) {
        cout << expected_x[i] << endl;
    }

    return 0;
}
