import java.util.*;
import java.lang.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        double[] x = new double[n];
        for (int i = 0; i < n; i++) {
            x[i] = sc.nextDouble();
        }
        int m = sc.nextInt();
        long k = sc.nextLong();
        int[] a = new int[m];
        for (int i = 0; i < m; i++) {
            a[i] = sc.nextInt() - 1;
        }
        double[][] mat = new double[n][n];
        for (int i = 0; i < n; i++) {
            mat[i][i] = 1;
        }
        for (int i = 0; i < m; i++) {
            int j = a[i];
            double[][] tmp = new double[n][n];
            for (int l = 0; l < n; l++) {
                for (int p = 0; p < n; p++) {
                    tmp[l][p] = mat[l][p];
                }
            }
            for (int l = 0; l < n; l++) {
                tmp[j][l] = 0.5 * tmp[j - 1][l] + 0.5 * tmp[j + 1][l];
            }
            tmp[j][j] = 0;
            for (int l = 0; l < n; l++) {
                for (int p = 0; p < n; p++) {
                    mat[l][p] = tmp[l][p];
                }
            }


        }
        double[][] res = mat;
        long cur = k;
        while (cur > 0) {
            if (cur % 2 == 1) {
                double[][] tmp = new double[n][n];
                for (int i = 0; i < n; i++) {
                    for (int j = 0; j < n; j++) {
                        tmp[i][j] = 0;
                        for (int p = 0; p < n; p++) {
                            tmp[i][j] += res[i][p] * mat[p][j];
                        }
                    }
                }
                res = tmp;

            }

            double[][] tmp = new double[n][n];
            for (int i = 0; i < n; i++) {
                for (int j = 0; j < n; j++) {
                    tmp[i][j] = 0;
                    for (int p = 0; p < n; p++) {
                        tmp[i][j] += mat[i][p] * mat[p][j];
                    }
                }
            }
            mat = tmp;
            cur /= 2;
        }


        for (int i = 0; i < n; i++) {
            double ans = 0;
            for (int j = 0; j < n; j++) {
                ans += res[i][j] * x[j];
            }
            System.out.println(ans);
        }
    }
}
