x = list(map(int, input().split()))
m, k = map(int, input().split())
a = list(map(int, input().split()))

if k == 0:
    for val in x:
        print(val)
else:
    mat = [[0.0] * n for _ in range(n)]
    for i in range(n):
        mat[i][i] = 1.0
    for _ in range(k):
        new_mat = [[0.0] * n for _ in range(n)]
        for i in range(n):
            for j in range(n):
                for l in range(n):
                    new_mat[i][j] += mat[i][l] * mat[l][j]
        mat = new_mat

    for j in range(m):
        idx = a[j]-1
        mat[idx][idx-1] = 0.5
        mat[idx][idx+1] = 0.5
        mat[idx][idx] = 0


    new_x = [0.0] * n
    for i in range(n):
        for j in range(n):
            new_x[i] += mat[i][j] * x[j]

    for val in new_x:
        print(val)

