#include <stdio.h>
#include <stdlib.h>

int main() {
    int N;
    scanf("%d", &N);
    long long a[1 << 17];
    for (int i = 0; i < (1 << N) - 1; i++) {
        scanf("%lld", &a[i]);
    }
    long long pyramid[1 << 17][1 << 17];
    for (int i = 0; i < (1 << N) - 1; i++) {
        pyramid[N - 1][i] = a[i];
    }
    for (int i = N - 2; i >= 0; i--) {
        for (int j = 0; j < (1 << i); j++) {
            long long arr[3];
            arr[0] = pyramid[i + 1][j * 2];
            arr[1] = pyramid[i + 1][j * 2 + 1];
            arr[2] = pyramid[i + 1][j * 2 + 2];
            long long temp[3];
            temp[0] = arr[0];
            temp[1] = arr[1];
            temp[2] = arr[2];
            for (int k = 0; k < 3; k++) {
                for (int l = 0; l < 2; l++) {
                    if (temp[l] > temp[l + 1]) {
                        long long t = temp[l];
                        temp[l] = temp[l + 1];
                        temp[l + 1] = t;
                    }
                }
            }
            pyramid[i][j] = temp[1];
        }
    }
    printf("%lld\n", pyramid[0][0]);
    return 0;
}
