#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<long long> a(1 << n);
  for (int i = 0; i < (1 << n) -1; ++i) {
    cin >> a[i];
  }

  vector<vector<long long>> pyramid(n + 1);
  pyramid[n] = a;

  for (int i = n - 1; i >= 0; --i) {
    pyramid[i].resize(1 << i);
    for (int j = 0; j < (1 << i); ++j) {
      vector<long long> vals;
      vals.push_back(pyramid[i + 1][2 * j]);
      vals.push_back(pyramid[i + 1][2 * j + 1]);
      vals.push_back(pyramid[i + 1][2 * j + 2]);
      sort(vals.begin(), vals.end());
      pyramid[i][j] = vals[1];
    }
  }

  cout << pyramid[0][0] << endl;
  return 0;
}
