#include <stdio.h>

int main() {
    int N;
    scanf("%d", &N);
    int a[3][N];
    for (int i = 0; i < 3; i++) {
        for (int j = 0; j < N; j++) {
            scanf("%d", &a[i][j]);
        }
    }
    int b[3][N];
    for (int i = 0; i < 3; i++) {
        for (int j = 0; j < N; j++) {
            b[i][j] = i + 3 * j + 1;
        }
    }
    int sum_a = 0, sum_b = 0;
    for (int i = 0; i < 3; i++) {
        for (int j = 0; j < N; j++) {
            sum_a += a[i][j];
            sum_b += b[i][j];
        }
    }
    if (sum_a == sum_b) {
        printf("Yes\n");
    } else {
        printf("No\n");
    }
    return 0;
}
