#include <iostream>
#include <vector>

using namespace std;

int main() {
    int n;
    cin >> n;

    vector<vector<long long>> a(3, vector<long long>(n));
    for (int i = 0; i < 3; ++i) {
        for (int j = 0; j < n; ++j) {
            cin >> a[i][j];
        }
    }

    vector<vector<long long>> b(3, vector<long long>(n));
    for (int i = 0; i < 3; ++i) {
        for (int j = 0; j < n; ++j) {
            b[i][j] = i + 3 * j + 1;
        }
    }

    vector<long long> count_a(3 * n + 1, 0);
    vector<long long> count_b(3 * n + 1, 0);
    for (int i = 0; i < 3; ++i) {
        for (int j = 0; j < n; ++j) {
            count_a[a[i][j]]++;
            count_b[b[i][j]]++;
        }
    }

    bool possible = true;
    for (int i = 1; i <= 3 * n; ++i) {
        if (count_a[i] != count_b[i]) {
            possible = false;
            break;
        }
    }

    if (possible) {
        cout << "Yes" << endl;
    } else {
        cout << "No" << endl;
    }

    return 0;
}
