import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[][] a = new int[3][n];
        for (int i = 0; i < 3; i++) {
            for (int j = 0; j < n; j++) {
                a[i][j] = sc.nextInt();
            }
        }
        int[][] b = new int[3][n];
        for (int i = 0; i < 3; i++) {
            for (int j = 0; j < n; j++) {
                b[i][j] = i + 3 * j + 3;
            }
        }
        List<Integer> listA = new ArrayList<>();
        List<Integer> listB = new ArrayList<>();
        for (int i = 0; i < 3; i++) {
            for (int j = 0; j < n; j++) {
                listA.add(a[i][j]);
                listB.add(b[i][j]);
            }
        }
        Collections.sort(listA);
        Collections.sort(listB);
        if (listA.equals(listB)) {
            System.out.println("Yes");
        } else {
            System.out.println("No");
        }
    }
}
