#include <stdio.h>
#include <stdlib.h>

int main() {
    int N, M;
    scanf("%d %d", &N, &M);
    int **grid = (int **)malloc(N * sizeof(int *));
    for (int i = 0; i < N; i++) {
        grid[i] = (int *)calloc(N, sizeof(int));
    }
    for (int i = 0; i < M; i++) {
        int a, b;
        scanf("%d %d", &a, &b);
        grid[a - 1][b - 1] = 1;
    }
    int changed = 1;
    while (changed) {
        changed = 0;
        for (int x = 0; x < N; x++) {
            for (int y = 0; y < N; y++) {
                if (grid[x][y] == 1) {
                    for (int z = 0; z < N; z++) {
                        if (grid[y][z] == 1 && grid[z][x] == 0) {
                            grid[z][x] = 1;
                            changed = 1;
                        }
                    }
                }
            }
        }
    }
    int count = 0;
    for (int i = 0; i < N; i++) {
        for (int j = 0; j < N; j++) {
            count += grid[i][j];
        }
    }
    printf("%d\n", count);
    for (int i = 0; i < N; i++) {
        free(grid[i]);
    }
    free(grid);
    return 0;
}

